"use client";

import { useEffect } from "react";
import { useSearchParams } from "next/navigation";
import FPSMeter from "@/components/runtime/FPSMeter";

export default function PerfGate() {
  const params = useSearchParams();
  const enabled = params?.get("debug") === "perf";

  useEffect(() => {
    if (!enabled) return;
    if (typeof PerformanceObserver === "undefined") return;
    // Long task observer to detect TBT hotspots
    // @ts-ignore - Longtask type not in lib.dom.d.ts by default in some TS targets
    const po = new PerformanceObserver((list) => {
      for (const entry of list.getEntries()) {
        console.warn("[longtask]", {
          duration: Math.round(entry.duration),
          name: entry.name,
        });
      }
    });
    try {
      // @ts-ignore
      po.observe({ type: "longtask", buffered: true });
    } catch {}
    return () => {
      try {
        po.disconnect();
      } catch {}
    };
  }, [enabled]);

  if (!enabled) return null;
  return <FPSMeter />;
}

"use client";

import { motion } from "framer-motion";
import { useMotionPrefs } from "@/components/providers/MotionProvider";
import {
  LuBolt,
  LuSparkles,
  LuCloudOff,
  LuFileDown,
  LuShieldCheck,
  LuCode,
} from "react-icons/lu";
import { springs, useWillChangeTransient } from "@/lib/motion";
import ScrollReveal from "@/components/ui/ScrollReveal";

const features = [
  {
    icon: LuBolt,
    title: "Add classes fast",
    desc: "Create and update classes in seconds.",
  },
  {
    icon: LuSparkles,
    title: "75% tracker",
    desc: "See how many classes to attend to reach the 75% target.",
  },
  {
    icon: LuCloudOff,
    title: "Offline friendly",
    desc: "Works even with spotty connections.",
  },
  {
    icon: LuFileDown,
    title: "Export (coming soon)",
    desc: "CSV/Excel export is on the way.",
  },
  { icon: LuShieldCheck, title: "Privacy-first", desc: "No creepy tracking." },
  { icon: LuCode, title: "Open-source", desc: "Trust the code." },
];

export default function Features() {
  const { reduced, coarse } = useMotionPrefs();
  const wc = useWillChangeTransient("transform");
  const hover =
    reduced || coarse
      ? {}
      : {
          whileHover: { y: -6, scale: 1.01, transition: springs.snappy },
        };

  return (
    <section className="px-4 sm:px-6 md:px-8 py-14 sm:py-18 md:py-24">
      <div className="mx-auto max-w-6xl">
        <ScrollReveal>
          <h2 className="text-center text-2xl sm:text-3xl font-semibold">
            Why Attendify
          </h2>
          <p className="mt-2 text-center text-sm text-[var(--color-muted)]">
            Fast, focused features that don't get in your way.
          </p>
        </ScrollReveal>

        <div className="mt-8 grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-4">
          {features.map((f, i) => (
            <motion.div
              key={i}
              onUpdate={wc.onStart}
              onAnimationComplete={wc.onComplete}
              className="rounded-xl border border-[var(--color-border)] bg-[var(--color-card)]/50 p-4 will-change-transform"
              transition={springs.snappy}
              {...hover}
            >
              <div className="flex items-start gap-3">
                <f.icon
                  className="size-5 text-[var(--color-accent-to)]"
                  aria-hidden
                />
                <div>
                  <h3 className="text-base font-medium">{f.title}</h3>
                  <p className="text-sm text-[var(--color-muted)]">{f.desc}</p>
                </div>
              </div>
            </motion.div>
          ))}
        </div>
      </div>
    </section>
  );
}

import fs from "node:fs/promises";
import path from "node:path";

export type Slide = { src: string; alt: string };

const ALLOWED_EXT = new Set([".png", ".jpg", ".jpeg", ".webp", ".svg"]);

export async function getScreenshots(): Promise<Slide[]> {
  const primary = path.join(process.cwd(), "src", "screenshots");
  const fallback = path.join(process.cwd(), "src", "screenshot");
  let base = primary;
  let entries: string[] = [];
  try {
    entries = await fs.readdir(primary);
  } catch {
    try {
      base = fallback;
      entries = await fs.readdir(fallback);
    } catch {
      return [];
    }
  }

  const files = entries
    .filter((f) => ALLOWED_EXT.has(path.extname(f).toLowerCase()))
    .sort((a, b) =>
      a.localeCompare(b, undefined, { numeric: true, sensitivity: "base" }),
    );

  return files.map((file) => ({
    src: `/screenshots/${encodeURIComponent(file)}`,
    alt: file.replace(/\.[^.]+$/, ""),
  }));
}
